let vardisplay = {run:()=>{const a=document.getElementsByTagName("html")[0],b=a.innerHTML.match(/({{(.*?)}})/g);for(let c=0;c<b.length;c++){const d=b[c],e=d.match(/([\<\>\=\;\$\(\)\,\/])/g);if(!e){const f=d.replace(/([{}<>\/\\\\=;$(),]*)/g,""),g=eval(f);a.innerHTML=a.innerHTML.replace(d,g)}}}};vardisplay.run();
